REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (22431, 0, 'É hora. O rito de exorcismo agora começará...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (22431, 1, 'Prepare você mesmo. Não permita a cerimônia para ser interrompido ou nós podemos perder nosso paciente...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (22431, 2, 'Seres atrás sujos de escuridão! Você não tem nenhum poder aqui!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (22431, 3, 'Eu... Não deve... Hesite ');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (22431, 4, 'A Luz é meu guia... é meu alimento!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (22431, 5, 'O poder de leve compells você! Atrás para sua cova!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (22431, 6, 'Seja limpo com Luz, humano! Não deixe a corrupção endiabrada subjugar você.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (22431, 7, 'Atrás! Eu lanço você de volta... Corruptor de fé! Autor de dor! Não retorne, ou sofra o mesmo destino que você fez aqui hoje!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (22432, 0, 'Mantenha-se afastado. O bobo é meu.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (22432, 1, 'Ah! Cesse os encantamentos, Anchorite! Cesse, ou eu mostrarei a você tal dor que suas pessoas patéticas nunca imaginaram!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (22432, 2, 'Isto é infrutífero, draenei! Você e seu pequeno ajudante não podem arrancar controle deste humano patético. Ele é meu!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (22432, 3, 'Eu vejo seus antepassados, Anchorite! Eles se estorcem e gritam na escuridão... Eles são conosco!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (22432, 4, 'Eu rasgarei sua alma em morsels e assado lento eles acima de fogo de demônio.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (14873, 0, 'Eu posso ir colecionar mais pedras para meu colar, Papai?');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (14873, 1, 'Dabu! Eu serei Papai cuidadoso!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (14873, 2, 'Ooohh... Esta pedra é boa e colorida. Vamos ver se eu posso achar mais como isto...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (14873, 3, 'Hmm... Aquela pedra é boa, mas muito pequeno.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (14873, 4, 'Isto é um shiney pedra. Eu porei isto em minha balde. Ok, eu necessidade justa alguns mais...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (14873, 5, 'Eu aposto meu amigo que Gruk gostaria desta pedra para a lança que ele está fazendo! É muito afiado... Eu espero que ele goste disto.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (14873, 6, 'Lá... Isso devia ser suficiente pedras para fazer um colar. Eu devia provavelmente chegar em casa agora de qualquer maneira.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (5907, 0, 'Sim você pode, Okla. Mas não faça perdida muito longe de casa.');
