REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (12277, 2, 'Eh Hornizz!  Eu volto!  E existem algumas pessoas atrás de mim que me ajudei de um aperto.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (12277, 3, 'Nós vamos ter que arranhar o Maraudines fora de nossa lista.  Muito duro de trabalho com...');
