REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (18340, 0, 'Eu farei que você paga por isto!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (19666, 2, 'Seu plano era um bom, Sha heen, e você cairia fora com ele se não em primeiro lugar...');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (19666, 3, 'Nunca menospreze o outro etéreo é cobiça!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (3446, 2, 'Eh $n!  Não esqueça de agarrar um engradado, um manual e uma vara de comando!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (20244, 0, '%s segura uma concha do mar até sua orelha.');
