REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (34320, 0, 'O %s pega a comida em sua boca e traga isto em um trago.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (31418, 3, 'Eu temo que a ira o consumirá, Thrall. Eu permaneço esperançada aquela razão prevalecerá, mas nós devemos nos preparar para o pior... Para guerra.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (31418, 4, 'Despedida, Warchief. Eu rezo que os da próxima vez nós encontramos será como alio.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (25866, 0, 'Os elementos esmagarão você!');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (16781, 5, 'Eu amo o firework mostrar no fim de Solstício de verão Firefestival.');
REPLACE INTO `creature_text` (`CreatureID`, `groupid`, `text`) VALUES (26520, 0, 'Obrigado novamente, $n, para este incenso deleitável.');
